	protected function stripSpacesAndCase($input){
		$input = str_replace(' ', '',$input);
		$input = strtolower($input);
		return $input;
	}

	protected function stripCoveredPostcodes(){
		$postcodes = $this->stripSpacesAndCase($this->postcodes);
		$trimmed = explode(',', $postcodes);
		return $trimmed;
	}

	public function checkPostcode($input){
		$input = $this->stripSpacesAndCase($input);
		$postcodes = $this->stripCoveredPostcodes();

		if(in_array($input, $postcodes)){
			session()->flash('postcode_success', 'true');
		}else {
			session()->flash('postcode_fail', 'true');
		}
	}
